INSERT INTO [dbo].[MATURITY_GROUPINGS] ([Grouping_Id], [Title], [Description], [Maturity_Model_Id], [Sequence], [Parent_Id], [Group_Level], [Type_Id], [Title_Id], [Abbreviation], [Title_Prefix]) VALUES (2637, N'Development', N'<p>The development phase includes elements of software and hardware creation and system fabrication and assembly. The Consequence-Focused Design principle can be applied to the development phase by considering the following questions:</p>', 17, 5, 2621, 3, 3, N'18', NULL, NULL)
INSERT INTO [dbo].[MATURITY_GROUPINGS] ([Grouping_Id], [Title], [Description], [Maturity_Model_Id], [Sequence], [Parent_Id], [Group_Level], [Type_Id], [Title_Id], [Abbreviation], [Title_Prefix]) VALUES (2638, N'Testing, Verification, Validation, and Deployment', N'<p>The testing, verification, validation, and deployment phase includes elements of unit testing and the integration and verification of the system and subsystems. The Consequence-Focused Design principle can be applied to the testing, verification, validation, and deployment phase by considering the following questions:</p>', 17, 6, 2621, 3, 3, N'19', NULL, NULL)
